    /**
     * Display a listing of the resource.
     *
     * @return \Illuminate\Http\Response
     */
    public function index()
    {
        $deals =  Deals::select('id')->where('user_id', Auth::user()->id)->get()->toArray();
        $data = ContactVendorDeal::with('user','deal')
                    ->whereHas('deal', function($q) use ($deals)
                    {
                        $q->whereIn('deal_id', $deals);
                    })->get();
        return view('vendor.contact_vendor.listing',compact('data'));
    }

